mapper.select:
select distinct e.dpt_AirPt_Cd,e.arrv_Airpt_Cd,e.flt_nbr, e.airCrft_Typ,e.dpt_AirPt_pot,e.lcl_Dpt_Tm,e.arrv_Airpt_pot, e.lcl_Arrv_Tm,e.days from eachflightinfo e
ORDER BY e.dpt_AirPt_Cd
limit #{offset},#{limit}

select

from eachflightinfo e


	And e.lcl_Dpt_Tm >= #{dptTmStart}


	And e.lcl_Dpt_Tm <= #{dptTmEnd}


	And e.lcl_Arrv_Tm >= #{arrvTmStart}


	And e.lcl_Arrv_Tm <= #{arrvTmEnd}



	ORDER BY e.flt_nbr ${fltNbrSort},e.lcl_Dpt_Tm ASC,e.lcl_Arrv_Tm ASC


	ORDER BY e.lcl_Dpt_Tm ${dptTmSort},e.lcl_Arrv_Tm ASC,e.flt_nbr ASC


	ORDER BY e.lcl_Arrv_Tm ${arrvTmSort},e.lcl_Dpt_Tm ASC,e.flt_nbr ASC


	limit #{offset},#{limit}

select distinct e.dpt_AirPt_Cd,e.arrv_Airpt_Cd,e.flt_nbr, e.airCrft_Typ,e.dpt_AirPt_pot,e.lcl_Dpt_Tm,e.arrv_Airpt_pot, e.lcl_Arrv_Tm,e.days from eachflightinfo e
ORDER BY e.dpt_AirPt_Cd
limit #{offset},#{limit}

